import React, { useState, useEffect } from "react";
import { Calendar as BigCalendar, momentLocalizer } from "react-big-calendar";
import moment from "moment";
import "react-big-calendar/lib/css/react-big-calendar.css";
import "./App.css";

import EventModal from "./components/EventModal";

import { useDispatch, useSelector } from "react-redux";
import { addEvent } from "./redux/eventSlice";
import DeleteModal from "./components/DeleteModal";


const localizer = momentLocalizer(moment);

function App() {
  const dispatch = useDispatch();
  const events = useSelector((state) => state.events.events);

  const [showModal, setShowModal] = useState(false);
  const [selectedSlot, setSelectedSlot] = useState(null);
  const [allEvents, setEvents] = useState([]);

  // for delete model 

  const [selectedEvent, setSelectedEvent] = useState(null);
const [showDeleteModal, setShowDeleteModal] = useState(false);


const handleEventSelect = (event) => {
  setSelectedEvent(event);
  setShowDeleteModal(true);
};




  // ✅ Fetch events from backend
  useEffect(() => {
    fetch("http://localhost:5000/events")
      .then((res) => res.json())
      .then((data) => setEvents(data))
      .catch((err) => console.error("Failed to fetch events", err));
  }, []);

  // ✅ Save new event to backend and Redux
  const handleAddEvent = (event) => {
    fetch("http://localhost:5000/events", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(event),
    })
      .then((res) => res.json())
      .then((data) => {
        dispatch(addEvent(data));
        setEvents((prev) => [...prev, data]);
        setShowModal(false);
      })
      .catch((err) => console.error("Failed to save event", err));
  };

  // ✅ Handle time slot selection
  const handleSelectSlot = (slotInfo) => {
    setSelectedSlot(slotInfo);
    setShowModal(true);
  };


  const handleDeleteEvent = async (eventId) => {
    try {
      await axios.delete(`http://localhost:5000/api/events/${eventId}`);
      setEvents(events.filter((event) => event._id !== eventId));
      setShowDeleteModal(false);
      setSelectedEvent(null);
    } catch (error) {
      console.error("Failed to delete event:", error);
    }
  };


  // ✅ Handle event deletion
  // const handleEventSelect = (event) => {


  //   if (window.confirm(`Delete event: "${event.title}"?`)) {
  //     fetch(`http://localhost:5000/events/${event._id}`, {
  //       method: "DELETE",
  //     })
  //       .then((res) => res.json())
  //       .then(() => {
  //         setEvents((prev) => prev.filter((e) => e._id !== event._id));
  //       })
  //       .catch((err) => console.error("Delete error:", err));
  //   }
  // };


  // const handleEventSelect = (event) => {
  //   setSelectedEvent(event);
  //   setShowDeleteModal(true);
  // };




  // cnfrm dlt 


  const confirmDelete = () => {
    fetch(`http://localhost:5000/events/${selectedEvent._id}`, {
      method: "DELETE",
    })
      .then(() => {
        setEvents((prev) => prev.filter((e) => e._id !== selectedEvent._id));
        setShowDeleteModal(false);
        setSelectedEvent(null);
      })
      .catch((err) => console.error("Delete error:", err));
  };



  {showDeleteModal && (
    <DeleteModal
      eventTitle={selectedEvent?.title}
      onCancel={() => setShowDeleteModal(false)}
      onConfirm={confirmDelete}
    />
  )}


  // ✅ Category-based colors
  const eventStyleGetter = (event) => {
    const categoryColors = {
      exercise: "#4CAF50",
      eating: "#FF9800",
      work: "#2196F3",
      relax: "#9C27B0",
      family: "#E91E63",
      social: "#FF5722",
    };
    return {
      style: {
        backgroundColor: categoryColors[event.category] || "#607D8B",
        borderRadius: "5px",
        color: "white",
        padding: "4px",
      },
    };
  };

  return (
    <div className="App">
      <h1 className="text-2xl font-bold text-center my-4">My Calendar</h1>
      <BigCalendar
        localizer={localizer}
        events={allEvents}
        startAccessor="start"
        endAccessor="end"
        selectable
        style={{ height: 600 }}
        onSelectSlot={handleSelectSlot}
        onSelectEvent={handleEventSelect}
        eventPropGetter={eventStyleGetter}
      />
      {showModal && (
        <EventModal
          onClose={() => setShowModal(false)}
          onSave={handleAddEvent}
          slotInfo={selectedSlot}
        />
      )}
    </div>
  );
}

export default App;
