import React from "react";

const DeleteModal = ({ eventTitle, onCancel, onConfirm }) => {
  return (
    <div className="modal-overlay">
      <div className="modal">
        <h3 className="text-lg font-bold mb-2">Delete Event</h3>
        <p className="mb-4">Are you sure you want to delete <strong>{eventTitle}</strong>?</p>
        <div className="flex justify-end gap-2">
          <button onClick={onCancel} className="btn btn-cancel">Cancel</button>
          <button onClick={onConfirm} className="btn btn-save bg-red-600">Delete</button>
        </div>
      </div>
    </div>
  );
};

export default DeleteModal;
